function Normalize(newName)
	{
		var Mynam = newName;
		var normalizer = Transliterator.getInstance("LATIN-ASCII; [:Nonspacing Mark:] Remove; LATIN-ASCII;");
		var newName = normalizer.transliterate(Mynam);
		task.logmsg("ISIM Person name attribute is:"+ newName );
		task.logmsg("==================");
		return newName;
	}
