//audit log
// building a class
Audit = function(filename){
	this.filename = filename;
	
	this.log = function(lvl,msg){
		if(typeof msg == "undefined"){
			msg = lvl;
			lvl = "INFO";
		}
		task.logmsg(lvl,msg);
		var file=system.openFileForAppend(this.filename);
		file.write(system.formatDate(new Date(),"yyyy-MM-dd'T'HH:mm:ss.SSS")+','
								+lvl+','+msg);
		file.newLine();
		file.close();						
	}
		
	
	return this;
}
